import { Component, OnInit } from '@angular/core';
import { TokenService } from 'src/app/service/token.service';

class Opciones {
  id: number;
  imagen: string;
  descripcion: string;
  url: string;
}

@Component({
  selector: 'app-inicio',
  templateUrl: './inicio.component.html',
  styleUrls: ['./inicio.component.css']
})
export class InicioComponent implements OnInit {

  nombreUsuario: string;
  isLogged = false;

  // carrusel con las opciones 
  opciones: Opciones[] = [];
  responsiveOptions;
  constructor(private tokenService: TokenService) {
    this.responsiveOptions = [
      {
        breakpoint: '1024px',
        numVisible: 3,
        numScroll: 3
      },
      {
        breakpoint: '768px',
        numVisible: 2,
        numScroll: 2
      },
      {
        breakpoint: '560px',
        numVisible: 1,
        numScroll: 1
      }
    ];
  }

  ngOnInit() {
    this.nombreUsuario = this.tokenService.getUserName();
    this.isLogged = this.tokenService.isLogged();

    this.opciones = [
      {
        id: 0,
        imagen: '',
        descripcion: 'Especialidad 1',
        url:'http://eduv.tecazuay.edu.ec'
      },
      {
        id: 1,
        imagen: 'data:image/jpeg;base64,',
        descripcion: 'Especialidad 2',
        url:'https://www.tecazuay.edu.ec/main/reglamentos.php'
      },
      {
        id: 2,
        imagen: 'data:image/jpeg;base64,',
        descripcion: 'Especialidad 3',
        url:'ubicaciones'
      },
      {
        id: 3,
        imagen: 'data:image/jpeg;base64,',
        descripcion: 'Especialidad 4',
        url:'https://www.tecazuay.edu.ec/main/ube.php'
      },
    ]
  }

  onLogOut(): void {
    this.tokenService.logOut();
  }


}
